import React, {useEffect} from 'react'
import { useParams } from 'react-router-dom';
import axios from 'axios';

function Post() {
    const { id } = useParams(); // useParams is a hook that allows you to access the URL parameters in the component.
    const [postObject, setPostObject] = React.useState({}); // useState is a hook that allows you to have state variables in functional components.

    useEffect(() => {
        axios.get(`http://localhost:3001/posts/byId/${id}`).then((response) => {
          setPostObject(response.data);
        });

        
    }, [id]);

  return (<div>{postObject.postText}</div>)
}

export default Post
