import React, {useEffect} from 'react'
import { useParams } from 'react-router-dom';
import axios from 'axios';

function Post() {
    const { id } = useParams(); // useParams is a hook that allows you to access the URL parameters in the component.
    const [postObject, setPostObject] = React.useState({}); // useState hook to store the post object
    const [comments, setComments] = React.useState([]); // useState hook to store the comments

    useEffect(() => {
        axios.get(`http://localhost:3001/posts/byId/${id}`).then((response) => {
          setPostObject(response.data);
        });

        axios.get(`http://localhost:3001/comments/byId/${id}`).then((response) => {
          setComments(response.data);
        });
    }, [id]);

  return (
    <div className="postPage">
      <div className="leftSide">
        <div className="post" id="individual">
        <div className="title"> {postObject.title} </div>
        <div className="postText"> {postObject.postText} </div>
        <div className="footer"> {postObject.username} </div>
        </div>
      </div>
      <div className="rightSide">
        <div className="addCommentContainer">
          <input type="text" placeholder="Comment..."  autoComplete="off" id="commentText" />
          <button> Add Comment </button>
        </div>
        <div className="listOfComments">
          // map through the comments array and display each comment 
          {comments.map((comment, key) => {
            return (
              <div key={key} className="comment"> {comment.commentText} </div>
            );
          })}
        </div>
      </div>
    </div>);
}

export default Post
